#Funciones:
def na_ratio(x):
    ratio_na = ((x.isnull().sum() / len(x))*100).sort_values(ascending = False)
    return ratio_na


def detect_outlier(x):
    outliers = []
    threshold=3
    mean_1 = np.mean(x)
    std_1 =np.std(x)
    for y in x:
        z_score= (y - mean_1)/std_1
        if np.abs(z_score) > threshold:
            outliers.append(y)
    print("Numero de Outliers: "+str(len(outliers))+" -- "+str(len(outliers)/len(x)))

